import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Menu from '@mui/material/Menu';
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import MenuItem from '@mui/material/MenuItem';
// import AdbIcon from '@mui/icons-material/Adb';
import { Link } from 'react-router-dom';
import { useContext } from "react"
import { AuthContext } from "../context/AuthContext"
// import { logout } from "../content/AuthContext"
function NavBarSection() {
  const  {currentUser}  = useContext(AuthContext)
  const { logout } = useContext(AuthContext)

  const pages = ['register' , 'login' ,'Home', 'Contact'];
  const pagesLogin = ['Profile','Home', 'Contact','write'];

  const [anchorElNav, setAnchorElNav] = React.useState(null);
  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };
  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };
const logouts = ()=>{
   logout()
}

  return (
    <AppBar position="sticky" style={{backgroundColor:"brown"}}>
      <Container maxWidth="xl">
        <Toolbar disableGutters>
          {/* <AdbIcon sx={{ display: { xs: 'none', md: 'flex' }, mr: 1 }} /> */}
          <Typography
            variant="h6"
            noWrap
            component="a"
            href="/"
            sx={{
              mr: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
            }}
          >
            <img alt="logo" src="data:image/png;base64," style={{height:"40px"}}/>
            &nbsp; {currentUser? <h3> {currentUser.name}</h3>:null}
          </Typography>

          <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
            <IconButton
              size="large"
              aria-label="account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleOpenNavMenu}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
            <Menu
              id="menu-appbar"
              anchorEl={anchorElNav}
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'left',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'left',
              }}
              open={Boolean(anchorElNav)}
              onClose={handleCloseNavMenu}
              sx={{
                display: { xs: 'block', md: 'none' },
                "^:hover":{color:"gray"}
              }}
            >
              {currentUser? pagesLogin.map((page) => (
            <Link to={"/"+page} style={{textDecoration:"none",color:"black","&:hover":{color:"gray"}}}> <MenuItem onClick={handleCloseNavMenu}>
                  <Typography textAlign="center" >{page}</Typography>
                </MenuItem>
                </Link>
              )) :  pagesLogin.map((page) => (
                <Link to={"/"+page} style={{textDecoration:"none",color:"black","&:hover":{color:"gray"}}}> <MenuItem onClick={handleCloseNavMenu}>
                      <Typography textAlign="center" >{page}</Typography>
                    </MenuItem>
                    </Link>
                  )) }
                  <hr/>
                 <span className="text-muted">&nbsp; Categories</span>
                  <Button
              href="/?cat=technology"
              sx={{ color: 'black', display: 'block' ,"&:hover":{color:"gray"}}}
            >
              Technology
            </Button>
            <Button
              href="/?cat=healt"
              sx={{ color: 'black', display: 'block' ,"&:hover":{color:"gray"}}}
            >
              Health
            </Button>
            <Button
              href="/?cat=art"
              sx={{ color: 'black', display: 'block' ,"&:hover":{color:"gray"}}}
            >
              Art
            </Button>
      {currentUser? <Button
        onClick={logouts}
        sx={{ color: 'black', display: 'block' ,"&:hover":{color:"gray"}}}
      >
        LogOut
      </Button>: null}
            </Menu>
          </Box>
          <Typography
            variant="h5"
            noWrap
            component="a"
            href=""
            sx={{
              mr: 2,
              display: { xs: 'flex', md: 'none' },
              flexGrow: 1,
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
            
              
            }}
          >
            SQL Project
          </Typography>
          <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } ,right:"0px" }}>
             {currentUser? pagesLogin.map((page) => (
              <Button
                key={page}
                onClick={handleCloseNavMenu}
                sx={{ my: 2, color: 'white', display: 'block' ,"&:hover":{fontWeight:"700",color:"black"}}}
                href={"/"+page} 
              >
                {page}
              </Button>
            )) 
            :pages.map((page) => (
              <Button
                key={page}
                onClick={handleCloseNavMenu}
                sx={{ my: 2, color: 'white', display: 'block' ,"&:hover":{fontWeight:"700",color:"black"}}}
                href={"/"+page} 
              >
                {page}
              </Button>
            ))}
            
            <Button
              href="/?cat=technology"
              sx={{ my: 2, color: 'white', display: 'block' ,"&:hover":{fontWeight:"700",color:"black"}}}
            >
              Technology
            </Button>
            <Button
              href="/?cat=healt"
              sx={{ my: 2, color: 'white', display: 'block' ,"&:hover":{fontWeight:"700",color:"black"}}}
            >
              Health
            </Button>
            <Button
              href="/?cat=art"
              sx={{ my: 2, color: 'white', display: 'block' ,"&:hover":{fontWeight:"700",color:"black"}}}
            >
              Art
            </Button>
      {currentUser? <Button
        onClick={logouts}
        sx={{ my: 2, color: 'white', display: 'block' ,"&:hover":{fontWeight:"700",color:"black"}}}
      >
        LogOut
      </Button>: null}
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
    // <>kjsadfhkasdj</>
  );
}
// export default ResponsiveAppBar;
export default NavBarSection;